"use strict";

$(document).ready(function () {
  $(".logo").click(function (e) {
    e.preventDefault(), window.location.reload();
  }), $(".burger").click(function (e) {
    e.preventDefault(), $(this).toggleClass("burger--active"), $("body").toggleClass("body-lock"), $(".menu").toggleClass("menu--active"), $(".header__inner").toggleClass("header__inner--active");
  });
  new Swiper(".locations-slider", {
    loop: !0,
    slidesPerView: 5,
    spaceBetween: 60,
    autoplay: !0,
    keyboard: {
      enabled: !0
    },
    breakpoints: {
      0: {
        slidesPerView: 1
      },
      576: {
        slidesPerView: 2
      },
      768: {
        slidesPerView: 3
      },
      992: {
        slidesPerView: 4
      },
      1180: {
        slidesPerView: 5
      }
    }
  });
  $(".review-slider").slick({
    dots: !0,
    arrows: !0,
    infinite: !1,
    speed: 300,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [{
      breakpoint: 1180,
      settings: {
        slidesToShow: 2
      }
    }, {
      breakpoint: 768,
      settings: {
        slidesToShow: 1
      }
    }]
  }), function () {
    var e = $("a, button"),
        o = $(".header__inner").outerHeight();
    e.click(function (e) {
      var s = e.target;

      if (s.hasAttribute("data-scrollto")) {
        var t = s.dataset.scrollto;
        e.preventDefault();
        var i = $(t).offset().top;
        window.scroll(0, i - o);
      }
    });
  }(), function () {
    var e = $(".header__outer"),
        o = (e.offset().top, e.next()),
        s = e.outerHeight(),
        t = Number(o.css("paddingTop").slice(0, -2)),
        i = s + t;
    document.querySelectorAll(".menu-link"), e.addClass("fixedToTop"), o.css("paddingTop", i);
  }();
});